mapper.update:
UPDATE board 
SET views = IFNULL(views, 0) + 1 
WHERE board_idx = #{board_idx}

UPDATE `board` 
SET
	name = #{name},
	category = #{category},
	content = #{content}
WHERE board_idx = #{board_idx}

mapper.select:
SELECT * FROM board


		category = #{category}


ORDER BY board_idx DESC
LIMIT #{start}, #{count}
